mapper.insert:
insert into product
values
(
	#{productId}
	, #{productName}
	, #{productPrice}
	, default
	, sysdate
	, #{productContent}
	, default
	, #{shopId}
	, #{categoryCode}
)

mapper.select:
select product_id , product_name , product_price , product_ynh , product_reg_date , product_content , product_del_flag , shop_id , category_code from product where product_del_flag = 'N'
and shop_id = #{shopId}
and product_id = #{productId}
and product_name like '%' || #{productName} || '%'
and product_ynh = #{productYnh}
and category_code = #{categoryCode}

select * from (select a.*, rownum as rnum from (select product_id , product_name , product_price , product_ynh , product_reg_date , product_content , product_del_flag , shop_id , category_code from product where product_del_flag = 'N'
and shop_id = #{shopId}
and product_id = #{productId}
and product_name like '%' || #{productName} || '%'
and product_ynh = #{productYnh}
and category_code = #{categoryCode}
)a ) where rnum >= 5 * (to_number(#{pageNum}) -1) +1 and rnum <= 5 * to_number(#{pageNum})

select 	count(*)
from 	product
where 	product_del_flag = 'N'

and 	shop_id = #{shopId}


and 	product_id = #{productId}


and 	product_name like '%' || #{productName} || '%'


and 	product_ynh = #{productYnh}


and 	category_code = #{categoryCode}

mapper.update:
update 	product
set 	product_ynh = #{productYnh}
where 	product_id = #{productId}

update 	product
set 	product_del_flag = 'Y'
where 	product_id = #{productId}

update 	product
set 	product_name = #{productName}
, 		category_code = #{categoryCode}
, 		product_price = #{productPrice}
, 		product_content = #{productContent}
where 	product_id = #{productId}
